import "./App.css";
import { useState } from "react";

function App() {
  const [mode, setMode] = useState("dark");
  const [count, setCounter] = useState(5);

  const reverter = (setMode) => {
    setTimeout(setMode("dark"), 5000);
  };

  console.log(mode);
  return (
    <div
      className={
        mode === "dark"
          ? `h-screen bg-neutral-800 grid place-items-center`
          : `h-screen bg-white grid place-items-center`
      }
    >
      <div className="flex justify-end w-full">
        <button className="p-2 rounded-md bg-neutral-700 text-neutral-300 mx-6">
          {mode === "light" ? (
            <svg
              onClick={() => {
                setMode("dark");
              }}
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke-width="1.5"
              stroke="currentColor"
              class="w-6 h-6"
            >
              <path
                stroke-linecap="round"
                stroke-linejoin="round"
                d="M21.752 15.002A9.718 9.718 0 0118 15.75c-5.385 0-9.75-4.365-9.75-9.75 0-1.33.266-2.597.748-3.752A9.753 9.753 0 003 11.25C3 16.635 7.365 21 12.75 21a9.753 9.753 0 009.002-5.998z"
              />
            </svg>
          ) : (
            <svg
              onClick={() => {
                setMode("light");
                reverter()
              }}
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke-width="1.5"
              stroke="currentColor"
              class="w-6 h-6"
            >
              <path
                stroke-linecap="round"
                stroke-linejoin="round"
                d="M12 3v2.25m6.364.386l-1.591 1.591M21 12h-2.25m-.386 6.364l-1.591-1.591M12 18.75V21m-4.773-4.227l-1.591 1.591M5.25 12H3m4.227-4.773L5.636 5.636M15.75 12a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0z"
              />
            </svg>
          )}
        </button>
      </div>
      <div className="w-[60%]">
        <div
          className={
            mode === "dark"
              ? `text-white w-full text-center text-[150%] my-[4%]`
              : `text-black w-full text-center text-[150%] my-[4%]`
          }
        >
          <p>"SOME PROFOUND QUOTE HERE"</p>
          <p>"Lightmode is for heretics, reverting in {count}"</p>
          <button>test</button>
        </div>
        <div className="bg-black h-[60vh]"></div>
      </div>
    </div>
  );
}

export default App;
